import React from "react";
import { withStyles } from '@material-ui/core/styles';

import { Map, Circle, TileLayer } from 'react-leaflet';


export default class Leaflet extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    const position = [35, -40];
    const zoom = 2;
    return (
      <Map center={position} zoom={zoom}>
        <TileLayer
          url={"https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"}
          attribution={'&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'}
        />
        {this.props.infectedOn &&
          <MyCircles data={this.props.infectedData} date={this.props.date} color="blue"/>
        }
        {this.props.recoveredOn &&
          <MyCircles data={this.props.recoveredData} date={this.props.date} color="green"/>
        }
        {this.props.deathOn &&
          <MyCircles data={this.props.deathData} date={this.props.date} color="red"/>
        }
      </Map>)
  }
}

const MyCircles = (props) => {
  return (
    props.data.map((row, i) => {
      if (row[props.date] <= 0) {
        // No cases on this date
        return;
      }
      if (row["Lat"] != null && row["Long"] != null) {
        return (
          <Circle
            key={i}
            center={[row["Lat"], row["Long"]]}
            radius={1000 * Math.sqrt(row[props.date])}
            fillOpacity={0.5}
            fillColor={props.color}
            stroke={false}
          />)
        }
      }
    )
  );
}
